import React from 'react';

class CarrinhoDeCompras extends React.Component {
  constructor() {
    super();
    this.state = {
      items: [],
    };
  }

  componentDidMount() {
  this.getCart()
  }

  onClickAddQuantity({ target : { quantity }}) {
    quantity: quantity + 1;
  }

  onClickRemoveQuantity({target}) {
    const { items } = this.state;
    if ( target.quantity > 1) {
      quantity: quantity - 1;
    } else {
      const remove = items.filter((products) => products !== target)
      this.setState({
        items: remove,
      })
    }
  }

  getCart() {
    const {items} = this.state;
    const getItem = JSON.parse(localStorage.getItem('product'))
    items.push(getItem);
  }
  render() {
    const { items } = this.state;

    const isCardEmpty = items.length > 0 
    ? items.map((item) => <div>
        <img src={item.thumbnail} alt={item.title} />
        <h3>{item.title}</h3>
        <p>{item.price}</p>
        <button onClick={this.onClickAddQuantity}>+</button>
        <p>{item.quantity}</p>
        <button onClick={this.onClickRemoveQuantity}>-</button>
      </div>)
      : <p data-testid="shopping-cart-empty-message">Seu carrinho está vazio</p>;

    return (
      <div>
        <section>
          { isCardEmpty }
        </section>
      </div>
    );
  }
}

export default CarrinhoDeCompras;
