import React from 'react';
import AddOnCart from './AddOnCart';

class CarrinhoDeCompras extends React.Component {
  constructor() {
    super();
    this.state = {
      produto: [],
    };

    this.getCart = this.getCart.bind(this);
  }

  componentDidMount() {
    this.getCart();
  }

  onClickAddQuantity = ({ target }) => {
    target = {
      quantity: target.quantity + 1,
    };
  }

  onClickRemoveQuantity =({ target }) => {
    const { produto } = this.state;
    if (target.quantity > 1) {
      target = {
        quantity: target.quantity - 1,
      };
    } else {
      const remove = produto.filter((products) => products !== target);
      this.setState({
        produto: remove,
      });
    }
  }

  getCart() {
    const getItem = JSON.parse(localStorage.getItem('product'));
    this.setState({
      produto: getItem,
    });
  }

  render() {
    const { produto } = this.state;
    return (
      <div>
        <section>
          { produto.length === 0 ? (produto.map((pro, index) => (
            <AddOnCart key={ index } product={ pro } />
          )))
            : <p data-testid="shopping-cart-empty">Seu carrinho está vazio</p>}
        </section>
      </div>
    );
  }
}

export default CarrinhoDeCompras;
