import React from 'react';
import AddOnCart from './AddOnCart';

class CarrinhoDeCompras extends React.Component {
  constructor() {
    super();
    this.state = {
      produto: [],
    };

    this.getCart = this.getCart.bind(this);
  }

  componentDidMount() {
    this.getCart();
  }

  getCart() {
    const getItem = JSON.parse(localStorage.getItem('product'));
    this.setState({
      produto: getItem,
    });
  }

  render() {
    const { produto } = this.state;
    return (
      <div>
        <section>
          { produto.length === 0 ? (produto.map((pro, index) => (
            <AddOnCart key={ index } product={ pro } data-testid="shopping-cart-product-name"/>
          )))
            : <p data-testid="shopping-cart-empty">Seu carrinho está vazio</p>}
        </section>
      </div>
    );
  }
}

export default CarrinhoDeCompras;
