import React from 'react';

class CarrinhoDeCompras extends React.Component {
  constructor() {
    super();
    this.state = {
      products: [],
    };
  }

  componentDidMount() {
  this.getCart()
  }

  onClickAddQuantity = ({ target }) => {
    target = {
      quantity: target.quantity + 1,
    }
  }

  onClickRemoveQuantity =( { target } ) => {
    const { products } = this.state;
    if ( target.quantity > 1 ) {
      target = {
      quantity: target.quantity - 1,
      }
    } else {
      const remove = products.filter((products) => products !== target)
      this.setState({
        products: remove,
      })
    }
  }

  getCart() {
    const getItem = JSON.parse(localStorage.getItem('product'))
    console.log(getItem)
    this.setState({
      products: getItem,
    });
  }
  render() {
    const { products } = this.state;
    console.log(products)
    const isCardEmpty = products.length > 0 
    ? products.map((item) => <div>
        <img src={item.item.thumbnail} alt={item.title} />
        <h3>{item.title}</h3>
        <p>{item.price}</p>
        <button onClick={this.onClickAddQuantity()}>+</button>
        <p>{item.quantity}</p>
        <button onClick={this.onClickRemoveQuantity()}>-</button>
      </div>)
      : <p data-testid="shopping-cart-empty-message">Seu carrinho está vazio</p>;

    return (
      <div>
        <section>
          { isCardEmpty }
        </section>
      </div>
    );
  }
}

export default CarrinhoDeCompras;
