  componentDidMount() {
  this.getCart()
  }

  getCart() {
    const {items, products} = this.state;
    const getItem = JSON.parse(localStorage.getItem('product'))
    items.push(getItem);
  }
  render() {
    const { items } = this.state;

    const isCardEmpty = items.length > 0 ? <p>lala</p>
      : <p data-testid="shopping-cart-empty-message">Seu carrinho está vazio</p>;

    return (
      <div>
        <section>
          { isCardEmpty }
