import React from 'react';

class CarrinhoDeCompras extends React.Component {
  constructor() {
    super();
    this.state = {
      produto: [],
    };
  }

  componentDidMount() {
  this.getCart()
  }

  onClickAddQuantity = ({ target }) => {
    target = {
      quantity: target.quantity + 1,
    }
  }

  onClickRemoveQuantity =( { target } ) => {
    const { produto } = this.state;
    if ( target.quantity > 1 ) {
      target = {
      quantity: target.quantity - 1,
      }
    } else {
      const remove = produto.filter((products) => products !== target)
      this.setState({
        produto: remove,
      })
    }
  }

  getCart() {
    const getItem = JSON.parse(localStorage.getItem('product'))
    this.setState({
      produto: getItem,
    });
  }
  render() {
    const { produto } = this.state;
    console.log(produto.item)
    const isCardEmpty = produto.length > 0 
    ? <h1>{produto.title}</h1>
      : <p data-testid="shopping-cart-empty-message">Seu carrinho está vazio</p>;

    return (
      <div>
        <section>
          { isCardEmpty }
        </section>
      </div>
    );
  }
}

export default CarrinhoDeCompras;
